import React, { useState } from "react";

const ProductList = () => {
  const [cartItems, setCartItems] = useState([]);
  const [isCartOpen, setIsCartOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");

  const products = [
    {
      id: 1,
      title: "–î–µ—Ç—Å–∫–∏–π –∫–∞—Å—Ç—é–º",
      description: "—Å –≤–æ–∑–≤—Ä–∞—Å—Ç–∞ 1-3",
      price: 2500,
      image: "https://th.bing.com/th/id/OIP.TNe1yN0pexX8H4d8rYkCLAHaHa?rs=1&pid=ImgDetMain",
    },
    {
      id: 2,
      title: "Chrome Hearts",
      description: "—Å –≤–æ–∑–≤—Ä–∞—Å—Ç–∞ 14-17",
      price: 7000,
      image: "https://th.bing.com/th/id/OIP.2IApXuv-ettWtusvZMbqiQHaHa?rs=1&pid=ImgDetMain",
    },
    {
      id: 3,
      title: "–•—É–¥–∏ Chrome Hearts",
      description: "—Å –≤–æ–∑–≤—Ä–∞—Å—Ç–∞ 20-",
      price: 700,
      image: "https://th.bing.com/th/id/OIP.81uniJLh06X1ffqX_dU7UAHaHa?w=192&h=191&c=7&r=0&o=5&dpr=1.4&pid=1.7",
    },
    {
      id: 4,
      title: "–ö–æ—Ç –Ω–∞ –æ—Ç–¥—ã—Ö–µ",
      description: "–õ–µ–∂–∏—Ç –∏ –∫–∞–π—Ñ—É–µ—Ç –≤–µ—Å—å –¥–µ–Ω—å.",
      price: 750,
      image: "https://placekitten.com/300/200?image=4",
    },
    {
      id: 5,
      title: "–ì—É—Ä–º–∞–Ω-–∫–æ—Ç",
      description: "–õ—é–±–∏—Ç —Ç–æ–ª—å–∫–æ –ª–æ—Å–æ—Å—å –∏ —Å–ª–∏–≤–∫–∏.",
      price: 850,
      image: "https://placekitten.com/300/200?image=5",
    },
    {
      id: 6,
      title: "–ö–æ—Ç-—Ñ–∏–ª–æ—Å–æ—Ñ",
      description: "–°–º–æ—Ç—Ä–∏—Ç –≤–¥–∞–ª—å –∏ –¥—É–º–∞–µ—Ç –æ –≤–µ—á–Ω–æ–º.",
      price: 620,
      image: "https://placekitten.com/300/200?image=6",
    },
  ];

  const toggleCart = () => {
    setIsCartOpen(!isCartOpen);
  };

  const addToCart = (product) => {
    setCartItems([...cartItems, product]);
  };

  const removeFromCart = (indexToRemove) => {
    setCartItems(cartItems.filter((_, index) => index !== indexToRemove));
  };

  const totalPrice = cartItems.reduce((sum, item) => sum + item.price, 0);

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const filteredProducts = products.filter(
    (product) =>
      product.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
      product.description.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div id="two">
      <form className="search-box" onSubmit={(e) => e.preventDefault()}>
        <input
          type="text"
          name="search"
          placeholder="–ü–æ–∏—Å–∫..."
          value={searchQuery}
          onChange={handleSearchChange}
        />
        <button type="submit" className="submit">
          üîç
        </button>
      </form>

      <button className="cart-toggle" onClick={toggleCart}>
        üõí
      </button>

      {isCartOpen && (
        <div className="shopping-cart" id="cartPanel">
          <h2 className="h2">–ú–æ—è –∫–æ—Ä–∑–∏–Ω–∞</h2>
          {cartItems.map((item, index) => (
            <div className="cart-item" key={index}>
              <img src={item.image} alt={item.title} />
              <div className="item-details">
                <h3>{item.title}</h3>
                <p>–¶–µ–Ω–∞: {item.price}–¢–≥</p>
              </div>
              <button className="remove-btn" onClick={() => removeFromCart(index)}>
                –£–¥–∞–ª–∏—Ç—å
              </button>
            </div>
          ))}
          <div className="cart-total">
            <strong>–ò—Ç–æ–≥–æ: {totalPrice}–¢–≥</strong>
          </div>
        </div>
      )}

      <div className="card-container">
        {filteredProducts.map((product) => (
          <div className="card" key={product.id}>
            <img src={product.image} alt="–ö–æ—Ç–∏–∫" />
            <div className="card-content">
              <h3>{product.title}</h3>
              <p>{product.description}</p>
              <p>–¶–µ–Ω–∞: {product.price}–¢–≥</p>
              <button onClick={() => addToCart(product)}>–î–æ–±–∞–≤–∏—Ç—å –≤ –∫–æ—Ä–∑–∏–Ω—É</button>
            </div>
          </div>
        ))}
        {filteredProducts.length === 0 && <p>–ù–∏—á–µ–≥–æ –Ω–µ –Ω–∞–π–¥–µ–Ω–æ</p>}
      </div>
    </div>
  );
};

export default ProductList;
