import React, { useState } from "react";

const ProductList = () => {
  const [cartItems, setCartItems] = useState([]);
  const [isCartOpen, setIsCartOpen] = useState(false);

  const products = Array.from({ length: 15 }, (_, i) => ({
    id: i + 1,
    title: `–ö–∞—Ä—Ç–æ—á–∫–∞ ${i + 1}`,
    description: "–ú–∏–ª—ã–π –∫–æ—Ç–∏–∫ –Ω–∞ –∫–∞—Ä—Ç–∏–Ω–∫–µ.",
    price: 500 + i * 50,
    image: `https://placekitten.com/300/200?image=${i + 1}`,
  }));

  const toggleCart = () => {
    setIsCartOpen(!isCartOpen);
  };

  const addToCart = (product) => {
    setCartItems([...cartItems, product]);
  };

  const removeFromCart = (indexToRemove) => {
    setCartItems(cartItems.filter((_, index) => index !== indexToRemove));
  };

  const totalPrice = cartItems.reduce((sum, item) => sum + item.price, 0);

  return (
    <div id="two">
      <form className="search-box" action="#" method="GET">
        <input type="text" name="search" placeholder="–ü–æ–∏—Å–∫..." />
        <button type="submit" className="submit">
          üîç
        </button>
      </form>

      <button className="cart-toggle" onClick={toggleCart}>
        üõí
      </button>

      {isCartOpen && (
        <div className="shopping-cart" id="cartPanel">
          <h2 className="h2">–ú–æ—è –∫–æ—Ä–∑–∏–Ω–∞</h2>
          {cartItems.map((item, index) => (
            <div className="cart-item" key={index}>
              <img src={item.image} alt={item.title} />
              <div className="item-details">
                <h3>{item.title}</h3>
                <p>–¶–µ–Ω–∞: {item.price}‚ÇΩ</p>
              </div>
              <button className="remove-btn" onClick={() => removeFromCart(index)}>
                –£–¥–∞–ª–∏—Ç—å
              </button>
            </div>
          ))}
          <div className="cart-total">
            <strong>–ò—Ç–æ–≥–æ: {totalPrice}‚ÇΩ</strong>
          </div>
        </div>
      )}

      <div className="card-container">
        {products.map((product) => (
          <div className="card" key={product.id}>
            <img src={product.image} alt="–ö–æ—Ç–∏–∫" />
            <div className="card-content">
              <h3>{product.title}</h3>
              <p>{product.description}</p>
              <p>–¶–µ–Ω–∞: {product.price}‚ÇΩ</p>
              <button onClick={() => addToCart(product)}>–î–æ–±–∞–≤–∏—Ç—å –≤ –∫–æ—Ä–∑–∏–Ω—É</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
