import React, { useState } from "react";

const ProductList = () => {
  const [cartItems, setCartItems] = useState([]);
  const [isCartOpen, setIsCartOpen] = useState(false);

  const products = [
    {
      id: 1,
      title: "–î–µ—Ç—Å–∫–∞",
      description: "–°–º–æ—Ç—Ä–∏—Ç –≤ –æ–∫–Ω–æ –∏ –º–µ—á—Ç–∞–µ—Ç –æ –≤–µ—Å–Ω–µ.",
      price: 500,
      image: "https://th.bing.com/th/id/OIP.TNe1yN0pexX8H4d8rYkCLAHaHa?rs=1&pid=ImgDetMain",
    },
    {
      id: 2,
      title: "–°–æ–Ω–Ω—ã–π –∫–æ—Ç",
      description: "–¢–æ–ª—å–∫–æ —á—Ç–æ –ø—Ä–æ—Å–Ω—É–ª—Å—è, –Ω–æ —É–∂–µ —É—Å—Ç–∞–ª.",
      price: 600,
      image: "https://placekitten.com/300/200?image=2",
    },
    {
      id: 3,
      title: "–ò–≥—Ä–∏–≤—ã–π –∫–æ—Ç–µ–Ω–æ–∫",
      description: "–ü—Ä—ã–≥–∞–µ—Ç –∏ –±–µ–≥–∞–µ—Ç –±–µ–∑ –æ—Å—Ç–∞–Ω–æ–≤–∫–∏!",
      price: 700,
      image: "https://placekitten.com/300/200?image=3",
    },
    {
      id: 4,
      title: "–ö–æ—Ç –Ω–∞ –æ—Ç–¥—ã—Ö–µ",
      description: "–õ–µ–∂–∏—Ç –∏ –∫–∞–π—Ñ—É–µ—Ç –≤–µ—Å—å –¥–µ–Ω—å.",
      price: 750,
      image: "https://placekitten.com/300/200?image=4",
    },
    {
      id: 5,
      title: "–ì—É—Ä–º–∞–Ω-–∫–æ—Ç",
      description: "–õ—é–±–∏—Ç —Ç–æ–ª—å–∫–æ –ª–æ—Å–æ—Å—å –∏ —Å–ª–∏–≤–∫–∏.",
      price: 850,
      image: "https://placekitten.com/300/200?image=5",
    },
    {
      id: 6,
      title: "–ö–æ—Ç-—Ñ–∏–ª–æ—Å–æ—Ñ",
      description: "–°–º–æ—Ç—Ä–∏—Ç –≤–¥–∞–ª—å –∏ –¥—É–º–∞–µ—Ç –æ –≤–µ—á–Ω–æ–º.",
      price: 620,
      image: "https://placekitten.com/300/200?image=6",
    },
  ];

  const toggleCart = () => {
    setIsCartOpen(!isCartOpen);
  };

  const addToCart = (product) => {
    setCartItems([...cartItems, product]);
  };

  const removeFromCart = (indexToRemove) => {
    setCartItems(cartItems.filter((_, index) => index !== indexToRemove));
  };

  const totalPrice = cartItems.reduce((sum, item) => sum + item.price, 0);

  return (
    <div id="two">
      <form className="search-box" action="#" method="GET">
        <input type="text" name="search" placeholder="–ü–æ–∏—Å–∫..." />
        <button type="submit" className="submit">
          üîç
        </button>
      </form>

      <button className="cart-toggle" onClick={toggleCart}>
        üõí
      </button>

      {isCartOpen && (
        <div className="shopping-cart" id="cartPanel">
          <h2 className="h2">–ú–æ—è –∫–æ—Ä–∑–∏–Ω–∞</h2>
          {cartItems.map((item, index) => (
            <div className="cart-item" key={index}>
              <img src={item.image} alt={item.title} />
              <div className="item-details">
                <h3>{item.title}</h3>
                <p>–¶–µ–Ω–∞: {item.price}‚ÇΩ</p>
              </div>
              <button className="remove-btn" onClick={() => removeFromCart(index)}>
